import React from "react";
import { Link } from "react-router-dom";

export default function Services() {
  const services = [
    {
      title: "Corporate Catering",
      icon: "corporate.png",
      description:
        "Daily meal solutions designed for corporate offices with customizable menus, hygiene assurance, and on-time delivery.",
    },
    {
      title: "Packed Meals",
      icon: "packed.png",
      description:
        "Convenient, hygienic, and fresh packed meals ideal for employees, events, and government setups.",
    },
    {
      title: "Event Catering",
      icon: "event.png",
      description:
        "From festivals to corporate gatherings, we offer themed and flexible catering for all occasions.",
    },
    {
      title: "Festival Menus",
      icon: "festival.png",
      description:
        "Specially curated menus for every celebration, ensuring your festive occasions are flavorful and memorable.",
    },
    {
      title: "Pop-Up Counters",
      icon: "popup.png",
      description:
        "Interactive pop-up food counters to engage and delight your guests with live cooking experiences.",
    },
    {
      title: "Tuck Shops",
      icon: "tuck.png",
      description:
        "On-site tuck shops with quick bite options, snacks, and beverages, customized for your organization.",
    },
  ];

  return (
    <div className="bg-gradient-to-br from-[#e0f7fa] to-[#e8f5e9] text-gray-800 font-sans">
      {/* Hero Section */}
      <section className="relative w-full h-[60vh] overflow-hidden">
        <img
          src="/hero1.jpg"
          alt="Services Hero"
          className="absolute inset-0 w-full h-full object-cover"
        />
        <div className="relative z-10 flex flex-col items-center justify-center h-full bg-black/60 text-white text-center px-6">
          <h1 className="text-5xl font-serif font-semibold">Our Services</h1>
        </div>
      </section>

      {/* Quote & Link */}
      <section className="bg-[#eafaf0] py-8 text-center">
        <p className="text-lg text-gray-800">
          Let’s craft the perfect catering solution for you —{" "}
          <Link to="/contact" className="text-blue-600 font-medium underline hover:text-green-600">
            touch here to get a personalized quote
          </Link>.
        </p>
      </section>

      {/* Services Grid */}
      <section className="py-12 px-6 max-w-6xl mx-auto">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-white shadow-md rounded-lg p-6 text-center hover:shadow-lg transition"
            >
              <img
                src={`/icons/${service.icon}`}
                alt={service.title}
                className="h-24 w-24 object-contain mx-auto mb-4"
              />
              <h3 className="text-lg font-semibold mb-2">{service.title}</h3>
              <p className="text-sm text-gray-600">{service.description}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
