import React, { useState } from "react";

export default function ContactUs() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    service: "",
    message: "",
  });

  const services = [
    "Corporate Catering",
    "Packed Meals",
    "Event Catering",
    "Festival Menus",
    "Tuck Shops",
    "Pop-Up Counters",
  ];

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Email validation regex
    const emailPattern = /^[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.(com|in|co\.in)$/;
    if (!emailPattern.test(form.email)) {
      alert("Please enter a valid email ending with .com, .in or .co.in");
      return;
    }

    try {
      const response = await fetch("https://mozris-backend.onrender.com/api/contact", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      const result = await response.json();

      if (response.ok) {
        alert("Message sent successfully!");
        setForm({ name: "", email: "", phone: "", service: "", message: "" });
      } else {
        alert(result.error || "Something went wrong. Please try again.");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error sending message. Please try again later.");
    }
  };

  const handleWhatsApp = () => {
    const phone = "917757803925";
    const message = `Hi Mozris,\nName: ${form.name}\nEmail: ${form.email}\nPhone: ${form.phone}\nService: ${form.service}\nMessage: ${form.message}`;
    const encodedMessage = encodeURIComponent(message);
    const whatsappUrl = `https://wa.me/${phone}?text=${encodedMessage}`;
    window.open(whatsappUrl, "_blank");
  };

  return (
    <div className="bg-gradient-to-br from-[#e0f7fa] to-[#e8f5e9] text-gray-800 font-sans">
      {/* Navbar */}
      <header className="flex items-center justify-between px-6 py-3 bg-black shadow-md sticky top-0 z-50 h-20">
        <img
          src="/mozris-logo.png"
          alt="Mozris Logo"
          className="max-h-16 md:max-h-20 w-auto object-contain"
        />
        <nav className="flex gap-6 font-medium text-white text-sm">
          <a href="/" className="hover:text-orange-400">Home</a>
          <a href="/about" className="hover:text-orange-400">About Us</a>
          <a href="/services" className="hover:text-orange-400">Services</a>
          <a href="/contact" className="hover:text-orange-400">Contact Us</a>
        </nav>
      </header>

      {/* Hero Section */}
      <section className="relative w-full h-[80vh] overflow-hidden">
        <img
          src="/hero1.jpg"
          alt="Mozris Hero"
          className="absolute inset-0 w-full h-full object-cover"
        />
        <div className="relative z-10 flex flex-col items-center justify-center h-full bg-black/60 text-white text-center px-6">
          <h1 className="text-5xl font-serif font-semibold">Contact Us</h1>
        </div>
      </section>

      {/* Contact Form */}
      <section className="py-16 px-6 max-w-4xl mx-auto">
        <h2 className="text-3xl font-bold text-center mb-10">Get in Touch</h2>
        <form onSubmit={handleSubmit} className="bg-white rounded-lg shadow-lg p-8 grid grid-cols-1 gap-6">
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={form.name}
            onChange={handleChange}
            required
            className="border border-gray-300 rounded px-4 py-2 w-full"
          />

          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            required
            pattern="^[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.(com|in|co\.in)$"
            className="border border-gray-300 rounded px-4 py-2 w-full"
          />

          <div className="flex items-center border border-gray-300 rounded px-2 py-2 bg-white">
            <span className="text-sm mr-2 flex items-center">
              <img src="/india-flag.png" alt="IN" className="h-4 w-6 mr-1" />
              +91
            </span>
            <input
              type="tel"
              name="phone"
              placeholder="10-digit mobile number"
              value={form.phone}
              onChange={handleChange}
              required
              className="w-full outline-none"
              maxLength="10"
              pattern="[0-9]{10}"
            />
          </div>

          <select
            name="service"
            value={form.service}
            onChange={handleChange}
            required
            className="border border-gray-300 rounded px-4 py-2 w-full"
          >
            <option value="">Select a Service</option>
            {services.map((service, idx) => (
              <option key={idx} value={service}>{service}</option>
            ))}
          </select>

          <textarea
            name="message"
            rows="5"
            placeholder="Message"
            value={form.message}
            onChange={handleChange}
            required
            className="border border-gray-300 rounded px-4 py-2 w-full"
          />

          <button
            type="submit"
            className="bg-green-700 hover:bg-green-800 text-white font-semibold py-2 px-6 rounded-md"
          >
            Send Message
          </button>

          <button
            type="button"
            onClick={handleWhatsApp}
            className="mt-2 text-center inline-block bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-md text-sm font-medium"
          >
            üí¨ Message us on WhatsApp
          </button>
        </form>
      </section>

